INSERT INTO `content` (`idContent`, `id_catalog`, `id_lang`, `type`, `content`) VALUES
(1, 1, 1, 'description', 'Dans un village à ciel ouvert, découvrez plus de 110 boutiques mode des plus grandes marques française et internationales, à prix réduits. \r\nOuvert 7 jours sur 7, de 10h à 20h, à 40 minutes de Paris et 5 minutes des parcs Disney®.\r\n\r\nProfitez de services 5 étoiles:\r\n- Accès facile depuis le centre de Paris avec la navette Shopping Express\r\n- Espace d\'Accueil avec personnel multilingue\r\n- Wifi gratuit\r\n- Shopping mains libres\r\n- Crédit de miles en faisant votre shopping dans le village\r\n- Restaurants et cafés\r\n- Borne de recharge téléphone\r\n- Consigne à bagages\r\n- Salon de détaxe'),
(2, 1, 2, 'description', 'In an open-air setting, discover more than 110 fashion boutiques from the most prestigious French and international brands, at reduced prices. \r\nOpen 7 days a week, from 10am to 8pm, 40 minutes from Paris and 5 minutes from Disney® parks.\r\n\r\nEnjoy 5-star services:\r\n- Easy access from the center of Paris with the Shopping Express(r) shuttle\r\n- Welcome Center with multilingual staff\r\n- Free wifi \r\n- Hands-free shopping\r\n- Earn air miles when you shop at La Vallée Village\r\n- Restaurants and cafés\r\n- Phone-charging station\r\n- Luggage drop\r\n- Tax-free lounge'),
(3, 1, 3, 'description', 'En un pueblo al aire libre, descubra más de 110 boutiques de moda de las mayores marcas francesas e internacionales, a precios reducidos.\r\nAbierto los 7 días de la semana, de 10 a.m. a 8 p.m., a 40 minutos de París y a 5 minutos de los parques Disney®.\r\n\r\nAproveche los servicios de 5 estrellas:\r\n- Fácil acceso desde el centro de París con el servicio de transporte Shopping Express\r\n- Área de recepción con personal multilingüe.\r\n- WiFi gratis\r\n- Compras manos libres\r\n- Millas de crédito mientras compras en el pueblo\r\n- Restaurantes y cafeterías\r\n- Estación de carga del teléfono\r\n- Almacenamiento de equipaje\r\n- Sala de devolución de impuestos\r\n'),
(4, 1, 4, 'description', 'Entdecken Sie in einem Open-Air-Dorf mehr als 110 Modeboutiquen der größten französischen und internationalen Marken zu ermäßigten Preisen.\r\n7 Tage die Woche von 10 bis 20 Uhr geöffnet, 40 Minuten von Paris und 5 Minuten von den Disney®-Parks entfernt.\r\n\r\nNutzen Sie den 5-Sterne-Service:\r\n- Einfacher Zugang vom Zentrum von Paris mit dem Shopping Express Shuttle\r\n- Empfangsbereich mit mehrsprachigem Personal\r\n- Kostenloses Wifi\r\n- Freisprechen beim Einkaufen\r\n- Meilengutschrift beim Einkauf im Dorf\r\n- Restaurants und Cafés\r\n- Telefonladestation\r\n- Gepäckaufbewahrung\r\n- Steuerrückerstattung Lounge\r\n\r\n'),
